
public class SwitchStatementExampleForRainbowColours {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("program to illutrate the rainbow colours\n");
		String colour = "black";
		switch (colour) {
		case "red":
			System.out.println("This red colour is present in rainbow ");
			break;
		case "orange":
			System.out.println("This orange colour is present in rainbow ");
			break;
		case "yellow":
			System.out.println("This yellow colour is present in rainbow ");
			break;
		case "green":
			System.out.println("This green colour is present in rainbow ");
			break;
		case "blue":
			System.out.println("This blue colour is present in rainbow ");
			break;
		case "indigo":
			System.out.println("This indigo colour is present in rainbow ");
			break;
		case "violet":
			System.out.println("This violet colour is present in rainbow ");
			break;
		default:
			System.out.println("This colour is not present in the rainbow");
		}

	}

}
