
public class ExampleToFindSumOfNaturalNumUsingForloop {

	public static void main(String[] args) {

		// TODO Auto-generated method stu
		int num,sum=0;
		for(num=5;num<=25;num++)
		{
			sum+=num;
			
		}
		System.out.println("sum of natural numbers between 5 to 25 is:"+sum);

	}

}
